import 'bootstrap-icons/font/bootstrap-icons.css';

function Alert({message, success}) {
  const color = '';
  if (success){
    const color = 'success';
  } else {
    const color = 'danger';
  }
  return (
    <div className={'alert alert-' + color}>
      {message}
    </div>     
  );
}

export default Alert;
