import React, { useEffect, useState } from "react";
import CategoryCard from "./CategoryCard";
import axios from "axios";

function AllCategoryCards() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('/getcategories')
      .then(res => {
        setCategories(res.data); // Update state with the fetched categories
      })
      .catch(err => {
        console.log(err);
      });
  }, []); // Empty dependency array to run once on mount

  const handleCategoryClick = (categoryId) => {
    axios.post('/getproducts', { categoryId })
      .then(res => {
        setProducts(res.data); 
        console.log(res);// Update state with the fetched products
      })
      .catch(err => {
        console.log(err);
      });
  };



  return (
    <div className="mx-5 mt-4">
      <div className="row row-cols-1 row-cols-md-5 g-4">
        {categories.map((category, index) => (
             <CategoryCard key={category.ID} category={category.category_name} description={category.category_description} 
             onClick={() => handleCategoryClick(category.ID)}
             />
        ))}
      </div>
    </div>
  );
}

export default AllCategoryCards;
