import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const Products = () => {
  const location = useLocation();

  // Extract categoryID from query params
  const query = new URLSearchParams(location.search);
  const categoryID = query.get('categoryID');

  // State to store products data
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch products based on categoryID
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);

        // Replace with your API endpoint
        const response = await fetch(`/products?categoryID=${categoryID}`);
        const data = await response.json();

        setProducts(data); // Store the fetched data
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Failed to fetch products');
      } finally {
        setLoading(false);
      }
    };

    if (categoryID) {
      fetchProducts();
    }
  }, [categoryID]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="container">
      <h1>Products</h1>
      {categoryID && <p>Category ID: {categoryID}</p>}
      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Products;
