import 'bootstrap-icons/font/bootstrap-icons.css';
import './Background.css'; 

function isActive(page, activePage) {
  return page === activePage ? 'active' : '';
}

function NavBar({ currentPage }) {
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-body-tertiary nav-background">
      <div className="container-fluid">
        <a className="navbar-brand" href="/">Shop</a>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-between" id="navbarSupportedContent">
          <ul className="navbar-nav mb-2 mb-lg-0">
            <li className="nav-item">
              <a className={`nav-link ${isActive('Home', currentPage)}`} aria-current="page" href="/">Home</a>
            </li>
            <li className="nav-item">
              <a className={`nav-link ${isActive('Link', currentPage)}`} aria-current="page" href="#">Link</a>
            </li>
            <li className="nav-item">
              <a className="nav-link disabled" aria-disabled="true">Disabled</a>
            </li>
          </ul>
          <ul className="navbar-nav mb-2 mb-lg-0">
            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                Dropdown
              </a>
              <ul className="dropdown-menu">
                <li><a className="dropdown-item" href="/signin">Action</a></li>
                <li><a className="dropdown-item" href="#">Another action</a></li>
                <li>
                  <a className="dropdown-item" onClick={() => {
                    // Delete the token from localStorage
                    localStorage.removeItem('token');
                    // Redirect to the login page
                    window.location.href = '/signin';
                  }}>
                    <i className="bi bi-box-arrow-right"></i> Logout
                  </a>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
