import React from 'react';

const GradientBackground = ({ isGradient }) => {
  const style = isGradient
    ? {
        backgroundColor: '#f9f9f9',
        backgroundImage: 'linear-gradient(180deg, #f9f9f9 29%, #9f80ec 100%)',
        height: '100vh',
      }
    : {
        backgroundColor: '#ffffff', // A different background if not using the gradient
        height: '100vh',
      };

  return <div style={style}>Your Content Here</div>;
};

export default GradientBackground;
