import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();

  // Extract categoryID from query params
  const query = new URLSearchParams(location.search);
  const categoryID = query.get('categoryID');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true); // Start loading
        const response = await axios.post('/getproducts', { categoryID: categoryID });
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Failed to fetch products. Please try again later.');
      } finally {
        setLoading(false); // End loading
      }
    };

    if (categoryID) {
      fetchProducts();
    } else {
      setLoading(false); // No category ID provided
      setError('No category ID provided in the query parameters.');
    }
  }, [categoryID]);

  return (
    <div className="container">
      <h1>Products</h1>
      {categoryID && <p>Category ID: {categoryID}</p>}

      {loading && <p>Loading products...</p>}

      {error && <p className="error">{error}</p>}

      {!loading && !error && products.length === 0 && <p>No products found for this category.</p>}

      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Products;
