import 'bootstrap-icons/font/bootstrap-icons.css';
import './Style.css'; 

function isActive(page, activePage) {
  return page === activePage ? 'active' : '';
}

function NavBar({ currentPage }) {
  return (
    //<nav className="navbar navbar-expand-lg navbar-light bg-body-tertiary nav-background">
      <nav class="navbar navbar-expand-lg bg-body-tertiary nav-background">
      <div className="container-fluid">
        <a className="navbar-brand" href="/">Shop</a>


        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <div className="navbar-nav mx-auto">
            <a 
              className={`nav-link ${isActive('Home', currentPage)}`} 
              href="/" 
              style={{ pointerEvents: 'none' }}>
              Home
            </a>
            <a className={`nav-link ${isActive('Link', currentPage)}`} href="#">Link</a>
          </div>
          <ul className="navbar-nav mb-2 mb-lg-0">
            <li className="nav-item dropdown dropstart">
              <a 
                className="nav-link dropdown-toggle" 
                id="navbarDropdown" 
                role="button" 
                data-bs-toggle="dropdown" 
                aria-expanded="false">
                Account
              </a>
              <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                <li><a className="dropdown-item" href="#"> <i class="bi bi-person"></i> Profile</a></li>
                <li><a className="dropdown-item" href="#">Another action</a></li>
                <li><hr class="dropdown-divider"></hr></li>
                <li>
                  <a 
                    className="dropdown-item" 
                    href="#" 
                    onClick={() => {
                      localStorage.removeItem('token');
                      window.location.href = '/signin';
                    }}>
                    <i className="bi bi-box-arrow-right"></i> Logout
                  </a>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default NavBar;
