import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Sidebar from '../components/SideBar';
import SearchBar from '../components/SearchBar';
import DisplayCard from '../components/DisplayCard'; 
import NavBar from '../components/NavBar';

const Products = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const categoryID = query.get('categoryID');

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      try {
        const response = await axios.post('/getproducts', { categoryID });
        setProducts(response.data);
      } catch (err) {
        console.error('Failed to fetch products:', err); // Log error for debugging
        setError('An error occurred while fetching products. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [categoryID]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <NavBar currentPage={'Products'} />
      <DisplayCard />
      <SearchBar />
      <div class="row">
      <div class="col-5">
      <Sidebar/>
      </div>
      <div class="col-">
      <h1>Products</h1>
      <p>Category ID: {categoryID}</p>
      {products.length === 0 ? (
        <p>No products found for this category.</p>
      ) : (
        <ul>
          {products.map(product => (
            <li key={product.product_ID}>{product.product_name}</li>
          ))}
        </ul>
      )}

      </div>




      </div>







    </div>
  );
};

export default Products;
